import React, { useCallback, useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  Box, 
  VStack, 
  Heading, 
  Text, 
  Button, 
  Icon, 
  Alert, 
  AlertIcon, 
  AlertTitle, 
  AlertDescription,
  useToast
} from '@chakra-ui/react';
import { Upload, FileText, AlertCircle } from 'lucide-react';
import { useFlashcard } from '../contexts/FlashcardContext';

export function UploadSection() {
  const { state, dispatch } = useFlashcard();
  const [isDragOver, setIsDragOver] = useState(false);
  const toast = useToast();

  const handleFileUpload = useCallback(async (file: File) => {
    if (!file.name.endsWith('.json')) {
      toast({
        title: 'Invalid file type',
        description: 'Please select a JSON file',
        status: 'error',
        duration: 5000,
        isClosable: true,
      });
      return;
    }

    dispatch({ type: 'SET_LOADING', payload: true });
    dispatch({ type: 'SET_ERROR', payload: null });

    try {
      const text = await file.text();
      const data = JSON.parse(text);

      if (!data.flashcards || !Array.isArray(data.flashcards)) {
        throw new Error('Invalid JSON format: missing flashcards array');
      }

      if (data.flashcards.length === 0) {
        throw new Error('No flashcards found in the JSON file');
      }

      dispatch({ type: 'SET_FLASHCARDS', payload: data.flashcards });
      dispatch({ type: 'SET_METADATA', payload: data.metadata || {} });

      toast({
        title: 'Success!',
        description: `Loaded ${data.flashcards.length} flashcards`,
        status: 'success',
        duration: 3000,
        isClosable: true,
      });
    } catch (error) {
      const errorMessage = `Failed to load JSON file: ${error instanceof Error ? error.message : 'Unknown error'}`;
      dispatch({ type: 'SET_ERROR', payload: errorMessage });

      toast({
        title: 'Error loading file',
        description: errorMessage,
        status: 'error',
        duration: 5000,
        isClosable: true,
      });
    }
  }, [dispatch, toast]);

  const handleDrop = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    setIsDragOver(false);

    const files = e.dataTransfer.files;
    if (files.length > 0) {
      handleFileUpload(files[0]);
    }
  }, [handleFileUpload]);

  const handleDragOver = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    setIsDragOver(true);
  }, []);

  const handleDragLeave = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    setIsDragOver(false);
  }, []);

  const handleFileInput = useCallback((e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      handleFileUpload(file);
    }
  }, [handleFileUpload]);

  if (state.flashcards.length > 0) {
    return null;
  }

  return (
    <AnimatePresence>
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        exit={{ opacity: 0, y: -20 }}
        transition={{ duration: 0.5 }}
      >
        <Box 
          bg="white" 
          rounded="lg" 
          shadow="lg" 
          p={8} 
          mb={8}
          textAlign="center"
        >
          <VStack spacing={6}>
            <motion.div
              initial={{ scale: 0 }}
              animate={{ scale: 1 }}
              transition={{ delay: 0.2, type: "spring", stiffness: 200 }}
            >
              <Icon as={Upload} w={16} h={16} color="gray.400" />
            </motion.div>

            <VStack spacing={4}>
              <Heading size="lg" color="gray.800">
                Load Flashcard JSON
              </Heading>

              <Text color="gray.600">
                Select a JSON file created by the PDF converter tool
              </Text>
            </VStack>

            <Box
              borderWidth={2}
              borderStyle="dashed"
              borderColor={isDragOver ? "blue.500" : "gray.300"}
              bg={isDragOver ? "blue.50" : "transparent"}
              rounded="lg"
              p={8}
              transition="all 0.2s"
              _hover={{ borderColor: "gray.400" }}
              onDrop={handleDrop}
              onDragOver={handleDragOver}
              onDragLeave={handleDragLeave}
            >
              <VStack spacing={4}>
                <label htmlFor="jsonFile" style={{ cursor: 'pointer' }}>
                  <motion.div
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                  >
                    <Button 
                      colorScheme="brand" 
                      size="lg"
                      leftIcon={<Icon as={FileText} />}
                    >
                      Choose JSON File
                    </Button>
                  </motion.div>
                </label>

                <input
                  type="file"
                  id="jsonFile"
                  accept=".json"
                  style={{ display: 'none' }}
                  onChange={handleFileInput}
                />

                <Text fontSize="sm" color="gray.500">
                  or drag and drop JSON file here
                </Text>
              </VStack>
            </Box>

            {/* Error Display */}
            <AnimatePresence>
              {state.error && (
                <motion.div
                  initial={{ opacity: 0, height: 0 }}
                  animate={{ opacity: 1, height: 'auto' }}
                  exit={{ opacity: 0, height: 0 }}
                  style={{ width: '100%' }}
                >
                  <Alert status="error" rounded="lg">
                    <AlertIcon />
                    <Box>
                      <AlertTitle>Error Loading File</AlertTitle>
                      <AlertDescription>{state.error}</AlertDescription>
                    </Box>
                  </Alert>
                </motion.div>
              )}
            </AnimatePresence>
          </VStack>
        </Box>
      </motion.div>
    </AnimatePresence>
  );
}
