import React from 'react';
import { motion } from 'framer-motion';
import { 
  Box, 
  VStack, 
  HStack, 
  Heading, 
  Text, 
  Icon,
  Code,
  useColorModeValue
} from '@chakra-ui/react';
import { HelpCircle } from 'lucide-react';

export function Instructions() {
  const bgColor = useColorModeValue('yellow.50', 'yellow.900');
  const borderColor = useColorModeValue('yellow.200', 'yellow.700');

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ delay: 0.5, duration: 0.5 }}
      style={{ width: '100%' }}
    >
      <Box
        bg={bgColor}
        borderWidth="1px"
        borderColor={borderColor}
        rounded="lg"
        p={6}
        mt={8}
      >
        <HStack spacing={3} mb={4}>
          <Icon as={HelpCircle} color="yellow.600" />
          <Heading size="md" color="yellow.800">
            ðŸ“– How to Use
          </Heading>
        </HStack>

        <VStack spacing={3} align="start">
          <Text fontSize="sm" color="yellow.700">
            <Text as="span" fontWeight="bold">
              Step 1:
            </Text>{' '}
            Convert your PDF using the command-line tool:
          </Text>

          <Code
            bg={useColorModeValue('yellow.100', 'yellow.800')}
            color={useColorModeValue('yellow.800', 'yellow.100')}
            px={2}
            py={1}
            rounded="md"
            fontSize="sm"
            display="block"
            w="100%"
          >
            python pdf_converter.py your_file.pdf
          </Code>

          <Text fontSize="sm" color="yellow.700">
            <Text as="span" fontWeight="bold">
              Step 2:
            </Text>{' '}
            Load the generated JSON file in this app
          </Text>

          <Text fontSize="sm" color="yellow.700">
            <Text as="span" fontWeight="bold">
              Step 3:
            </Text>{' '}
            Study with interactive flashcards!
          </Text>
        </VStack>
      </Box>
    </motion.div>
  );
}
