import React from 'react';
import { motion } from 'framer-motion';
import { HelpCircle } from 'lucide-react';

export function Instructions() {
  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ delay: 0.5, duration: 0.5 }}
      className="bg-yellow-50 border border-yellow-200 rounded-lg p-6 mt-8"
    >
      <div className="flex items-center mb-3">
        <HelpCircle className="h-5 w-5 text-yellow-600 mr-2" />
        <h3 className="text-lg font-semibold text-yellow-800">
          ðŸ“– How to Use
        </h3>
      </div>

      <div className="text-sm text-yellow-700 space-y-2">
        <p><strong>Step 1:</strong> Convert your PDF using the command-line tool:</p>
        <code className="bg-yellow-100 px-2 py-1 rounded block">
          python pdf_converter.py your_file.pdf
        </code>
        <p><strong>Step 2:</strong> Load the generated JSON file in this app</p>
        <p><strong>Step 3:</strong> Study with interactive flashcards!</p>
      </div>
    </motion.div>
  );
}
