import React from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  Box, 
  HStack, 
  Heading, 
  Text, 
  Icon,
  SimpleGrid,
  useColorModeValue
} from '@chakra-ui/react';
import { Info } from 'lucide-react';
import { useFlashcard } from '../contexts/FlashcardContext';

export function MetadataDisplay() {
  const { state } = useFlashcard();
  const bgColor = useColorModeValue('blue.50', 'blue.900');
  const borderColor = useColorModeValue('blue.200', 'blue.700');
  const cardBg = useColorModeValue('white', 'gray.800');
  const cardBorderColor = useColorModeValue('gray.200', 'gray.700');

  if (!state.metadata) {
    return null;
  }

  const metadata = state.metadata;
  const metadataItems = [
    { label: 'üìÑ Source PDF', value: metadata.source_pdf ? getFilename(metadata.source_pdf) : 'Unknown' },
    { label: 'üìä Total Pages', value: metadata.total_pages || 'Unknown' },
    { label: 'üìù Format Type', value: metadata.format_type || 'Unknown' },
    { label: '‚ùì Questions Extracted', value: metadata.questions_extracted || 'Unknown' },
    { label: 'üîë Answer Key Entries', value: metadata.answer_key_entries || 'Unknown' },
    { label: 'üéØ Valid Flashcards', value: metadata.valid_flashcards || state.flashcards.length }
  ];

  return (
    <AnimatePresence>
      <motion.div
        initial={{ opacity: 0, height: 0 }}
        animate={{ opacity: 1, height: 'auto' }}
        exit={{ opacity: 0, height: 0 }}
        transition={{ duration: 0.3 }}
        style={{ width: '100%' }}
      >
        <Box
          bg={bgColor}
          borderWidth="1px"
          borderColor={borderColor}
          rounded="lg"
          p={6}
          mt={8}
        >
          <HStack spacing={3} mb={4}>
            <Icon as={Info} color="blue.600" />
            <Heading size="md" color="blue.800">
              üìã Flashcard Set Information
            </Heading>
          </HStack>

          <SimpleGrid columns={{ base: 1, md: 3 }} spacing={4}>
            {metadataItems.map((item, index) => (
              <motion.div
                key={item.label}
                initial={{ opacity: 0, x: -20 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ delay: index * 0.1 }}
              >
                <Box
                  bg={cardBg}
                  p={3}
                  rounded="md"
                  borderWidth="1px"
                  borderColor={cardBorderColor}
                >
                  <Text fontSize="sm">
                    <Text as="span" fontWeight="bold">
                      {item.label}:
                    </Text>{' '}
                    {item.value}
                  </Text>
                </Box>
              </motion.div>
            ))}
          </SimpleGrid>
        </Box>
      </motion.div>
    </AnimatePresence>
  );
}

function getFilename(path: string): string {
  return path.split('/').pop()?.split('\\').pop() || 'Unknown';
}
