import React from 'react';
import { motion } from 'framer-motion';
import { 
  Box, 
  Container, 
  Heading, 
  Text, 
  VStack,
  useColorModeValue
} from '@chakra-ui/react';
import { FlashcardProvider, useFlashcard } from './contexts/FlashcardContext';
import { UploadSection } from './components/UploadSection';
import { FlashcardSection } from './components/FlashcardSection';
import { Instructions } from './components/Instructions';

function AppContent() {
  const { state } = useFlashcard();

  return (
    <Box 
      minH="100vh" 
      bgGradient="linear(to-br, blue.50, indigo.100)"
      py={8}
    >
      <Container maxW="4xl" px={4}>
        {/* Header */}
        <motion.div
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
        >
          <VStack spacing={4} mb={8} textAlign="center">
            <Heading 
              size="2xl" 
              color="gray.800"
              fontWeight="bold"
            >
              ðŸŽ“ Flashcard App
            </Heading>
            <Text 
              fontSize="lg" 
              color="gray.600"
            >
              {state.flashcards.length > 0 
                ? `Studying ${state.flashcards.length} flashcards`
                : "Your smart study companion"
              }
            </Text>
          </VStack>
        </motion.div>

        {/* Main Content */}
        <UploadSection />
        <FlashcardSection />
        <Instructions />
      </Container>
    </Box>
  );
}

function App() {
  return (
    <FlashcardProvider>
      <AppContent />
    </FlashcardProvider>
  );
}

export default App;
