import React from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Info } from 'lucide-react';
import { useFlashcard } from '../contexts/FlashcardContext';

export function MetadataDisplay() {
  const { state } = useFlashcard();

  if (!state.metadata) {
    return null;
  }

  const metadata = state.metadata;
  const metadataItems = [
    { label: 'üìÑ Source PDF', value: metadata.source_pdf ? getFilename(metadata.source_pdf) : 'Unknown' },
    { label: 'üìä Total Pages', value: metadata.total_pages || 'Unknown' },
    { label: 'üìù Format Type', value: metadata.format_type || 'Unknown' },
    { label: '‚ùì Questions Extracted', value: metadata.questions_extracted || 'Unknown' },
    { label: 'üîë Answer Key Entries', value: metadata.answer_key_entries || 'Unknown' },
    { label: 'üéØ Valid Flashcards', value: metadata.valid_flashcards || state.flashcards.length }
  ];

  return (
    <AnimatePresence>
      <motion.div
        initial={{ opacity: 0, height: 0 }}
        animate={{ opacity: 1, height: 'auto' }}
        exit={{ opacity: 0, height: 0 }}
        transition={{ duration: 0.3 }}
        className="bg-blue-50 border border-blue-200 rounded-lg p-6 mt-8"
      >
        <div className="flex items-center mb-3">
          <Info className="h-5 w-5 text-blue-600 mr-2" />
          <h3 className="text-lg font-semibold text-blue-800">
            üìã Flashcard Set Information
          </h3>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-sm">
          {metadataItems.map((item, index) => (
            <motion.div
              key={item.label}
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ delay: index * 0.1 }}
              className="bg-white p-3 rounded"
            >
              <strong>{item.label}:</strong> {item.value}
            </motion.div>
          ))}
        </div>
      </motion.div>
    </AnimatePresence>
  );
}

function getFilename(path: string): string {
  return path.split('/').pop()?.split('\\').pop() || 'Unknown';
}
