import React, { useCallback, useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  Box, 
  VStack, 
  HStack,
  Heading, 
  Text, 
  Button, 
  Icon, 
  Alert, 
  AlertIcon, 
  AlertTitle, 
  AlertDescription,
  useToast,
  Select,
  Badge,
  Divider,
  useColorModeValue
} from '@chakra-ui/react';
import { Upload, FileText, FolderOpen, RefreshCw } from 'lucide-react';
import { useFlashcard } from '../contexts/FlashcardContext';

export function UploadSection() {
  const { state, dispatch } = useFlashcard();
  const [isDragOver, setIsDragOver] = useState(false);
  const [availableFiles, setAvailableFiles] = useState<string[]>([]);
  const [selectedFile, setSelectedFile] = useState<string>('');
  const [isScanning, setIsScanning] = useState(false);
  const toast = useToast();

  const bgColor = useColorModeValue('white', 'gray.800');
  const borderColor = useColorModeValue('gray.200', 'gray.700');

  // Scan for JSON files on component mount
  useEffect(() => {
    scanForJsonFiles();
  }, []);

  const scanForJsonFiles = useCallback(async () => {
    setIsScanning(true);
    try {
      // In a real app, you'd scan the filesystem
      // For now, we'll simulate finding files
      const mockFiles = ['flashcards.json', 'exam_questions.json', 'study_set.json'];
      setAvailableFiles(mockFiles);

      // Auto-select the first file if available
      if (mockFiles.length > 0 && !selectedFile) {
        setSelectedFile(mockFiles[0]);
        await loadSelectedFile(mockFiles[0]);
      }
    } catch (error) {
      console.log('No JSON files found');
    } finally {
      setIsScanning(false);
    }
  }, [selectedFile]);

  const loadSelectedFile = useCallback(async (filename: string) => {
    if (!filename) return;

    dispatch({ type: 'SET_LOADING', payload: true });
    dispatch({ type: 'SET_ERROR', payload: null });

    try {
      // In a real app, you'd load the actual file
      // For demo purposes, we'll simulate loading
      const mockData = {
        flashcards: [
          {
            question: "What is the capital of France?",
            answer: "Paris",
            question_number: 1,
            format: "multiple_choice" as const,
            all_options: {
              "A": "Paris",
              "B": "London", 
              "C": "Berlin",
              "D": "Madrid"
            }
          },
          {
            question: "What is 2 + 2?",
            answer: "4",
            question_number: 2,
            format: "multiple_choice" as const,
            all_options: {
              "A": "3",
              "B": "4",
              "C": "5", 
              "D": "6"
            }
          }
        ],
        metadata: {
          source_pdf: filename,
          total_pages: 10,
          format_type: "multiple_choice",
          questions_extracted: 50,
          answer_key_entries: 50,
          valid_flashcards: 2
        }
      };

      dispatch({ type: 'SET_FLASHCARDS', payload: mockData.flashcards });
      dispatch({ type: 'SET_METADATA', payload: mockData.metadata });

      toast({
        title: 'Success!',
        description: `Loaded ${mockData.flashcards.length} flashcards from ${filename}`,
        status: 'success',
        duration: 3000,
        isClosable: true,
      });
    } catch (error) {
      const errorMessage = `Failed to load ${filename}: ${error instanceof Error ? error.message : 'Unknown error'}`;
      dispatch({ type: 'SET_ERROR', payload: errorMessage });

      toast({
        title: 'Error loading file',
        description: errorMessage,
        status: 'error',
        duration: 5000,
        isClosable: true,
      });
    } finally {
      dispatch({ type: 'SET_LOADING', payload: false });
    }
  }, [dispatch, toast]);

  const handleFileUpload = useCallback(async (file: File) => {
    if (!file.name.endsWith('.json')) {
      toast({
        title: 'Invalid file type',
        description: 'Please select a JSON file',
        status: 'error',
        duration: 5000,
        isClosable: true,
      });
      return;
    }

    dispatch({ type: 'SET_LOADING', payload: true });
    dispatch({ type: 'SET_ERROR', payload: null });

    try {
      const text = await file.text();
      const data = JSON.parse(text);

      if (!data.flashcards || !Array.isArray(data.flashcards)) {
        throw new Error('Invalid JSON format: missing flashcards array');
      }

      if (data.flashcards.length === 0) {
        throw new Error('No flashcards found in the JSON file');
      }

      dispatch({ type: 'SET_FLASHCARDS', payload: data.flashcards });
      dispatch({ type: 'SET_METADATA', payload: data.metadata || {} });

      toast({
        title: 'Success!',
        description: `Loaded ${data.flashcards.length} flashcards from ${file.name}`,
        status: 'success',
        duration: 3000,
        isClosable: true,
      });
    } catch (error) {
      const errorMessage = `Failed to load JSON file: ${error instanceof Error ? error.message : 'Unknown error'}`;
      dispatch({ type: 'SET_ERROR', payload: errorMessage });

      toast({
        title: 'Error loading file',
        description: errorMessage,
        status: 'error',
        duration: 5000,
        isClosable: true,
      });
    }
  }, [dispatch, toast]);

  const handleDrop = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    setIsDragOver(false);

    const files = e.dataTransfer.files;
    if (files.length > 0) {
      handleFileUpload(files[0]);
    }
  }, [handleFileUpload]);

  const handleDragOver = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    setIsDragOver(true);
  }, []);

  const handleDragLeave = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    setIsDragOver(false);
  }, []);

  const handleFileInput = useCallback((e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      handleFileUpload(file);
    }
  }, [handleFileUpload]);

  const handleFileSelect = useCallback((filename: string) => {
    setSelectedFile(filename);
    loadSelectedFile(filename);
  }, [loadSelectedFile]);

  if (state.flashcards.length > 0) {
    return null;
  }

  return (
    <AnimatePresence>
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        exit={{ opacity: 0, y: -20 }}
        transition={{ duration: 0.5 }}
        style={{ width: '100%' }}
      >
        <Box 
          bg={bgColor} 
          rounded="lg" 
          shadow="lg" 
          p={8} 
          mb={8}
          borderWidth="1px"
          borderColor={borderColor}
        >
          <VStack spacing={6} textAlign="center">
            <motion.div
              initial={{ scale: 0 }}
              animate={{ scale: 1 }}
              transition={{ delay: 0.2, type: "spring", stiffness: 200 }}
            >
              <Icon as={FolderOpen} w={16} h={16} color="blue.500" />
            </motion.div>

            <VStack spacing={4}>
              <Heading size="lg" color="gray.800">
                Welcome to Flashcard App! üéì
              </Heading>

              <Text color="gray.600" fontSize="lg">
                Let's find your study materials
              </Text>
            </VStack>

            {/* Available Files Section */}
            {availableFiles.length > 0 && (
              <Box w="100%" textAlign="left">
                <HStack justify="space-between" mb={3}>
                  <Text fontWeight="semibold" color="gray.700">
                    üìÅ Available Flashcard Sets
                  </Text>
                  <Button
                    size="sm"
                    variant="ghost"
                    leftIcon={<Icon as={RefreshCw} />}
                    onClick={scanForJsonFiles}
                    isLoading={isScanning}
                  >
                    Refresh
                  </Button>
                </HStack>

                <VStack spacing={3} align="stretch">
                  {availableFiles.map((filename, index) => (
                    <motion.div
                      key={filename}
                      initial={{ opacity: 0, x: -20 }}
                      animate={{ opacity: 1, x: 0 }}
                      transition={{ delay: index * 0.1 }}
                    >
                      <Box
                        p={4}
                        borderWidth="1px"
                        borderColor={selectedFile === filename ? "blue.300" : "gray.200"}
                        bg={selectedFile === filename ? "blue.50" : "transparent"}
                        rounded="md"
                        cursor="pointer"
                        _hover={{ 
                          borderColor: "blue.300",
                          bg: "blue.50"
                        }}
                        onClick={() => handleFileSelect(filename)}
                        transition="all 0.2s"
                      >
                        <HStack justify="space-between">
                          <HStack spacing={3}>
                            <Icon as={FileText} color="blue.500" />
                            <Text fontWeight="medium">{filename}</Text>
                          </HStack>
                          {selectedFile === filename && (
                            <Badge colorScheme="blue" variant="subtle">
                              Selected
                            </Badge>
                          )}
                        </HStack>
                      </Box>
                    </motion.div>
                  ))}
                </VStack>
              </Box>
            )}

            {/* Divider */}
            <Divider />

            {/* Upload New File Section */}
            <Box w="100%" textAlign="left">
              <Text fontWeight="semibold" color="gray.700" mb={3}>
                üì§ Upload New Flashcard Set
              </Text>

              <Box
                borderWidth={2}
                borderStyle="dashed"
                borderColor={isDragOver ? "blue.500" : "gray.300"}
                bg={isDragOver ? "blue.50" : "transparent"}
                rounded="lg"
                p={6}
                transition="all 0.2s"
                _hover={{ borderColor: "gray.400" }}
                onDrop={handleDrop}
                onDragOver={handleDragOver}
                onDragLeave={handleDragLeave}
              >
                <VStack spacing={4}>
                  <label htmlFor="jsonFile" style={{ cursor: 'pointer' }}>
                    <motion.div
                      whileHover={{ scale: 1.05 }}
                      whileTap={{ scale: 0.95 }}
                    >
                      <Button 
                        colorScheme="brand" 
                        size="lg"
                        leftIcon={<Icon as={Upload} />}
                      >
                        Choose JSON File
                      </Button>
                    </motion.div>
                  </label>

                  <input
                    type="file"
                    id="jsonFile"
                    accept=".json"
                    style={{ display: 'none' }}
                    onChange={handleFileInput}
                  />

                  <Text fontSize="sm" color="gray.500">
                    or drag and drop JSON file here
                  </Text>
                </VStack>
              </Box>
            </Box>

            {/* Error Display */}
            <AnimatePresence>
              {state.error && (
                <motion.div
                  initial={{ opacity: 0, height: 0 }}
                  animate={{ opacity: 1, height: 'auto' }}
                  exit={{ opacity: 0, height: 0 }}
                  style={{ width: '100%' }}
                >
                  <Alert status="error" rounded="lg">
                    <AlertIcon />
                    <Box>
                      <AlertTitle>Error Loading File</AlertTitle>
                      <AlertDescription>{state.error}</AlertDescription>
                    </Box>
                  </Alert>
                </motion.div>
              )}
            </AnimatePresence>
          </VStack>
        </Box>
      </motion.div>
    </AnimatePresence>
  );
}
