import React from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  HStack, 
  Button, 
  Text, 
  Icon,
  Menu,
  MenuButton,
  MenuList,
  MenuItem,
  useDisclosure
} from '@chakra-ui/react';
import { 
  ChevronLeft, 
  ChevronRight, 
  Shuffle, 
  RotateCcw,
  FolderOpen,
  ChevronDown
} from 'lucide-react';
import { useFlashcard } from '../contexts/FlashcardContext';
import { Flashcard } from './Flashcard';
import { MetadataDisplay } from './MetadataDisplay';

export function FlashcardSection() {
  const { state, dispatch } = useFlashcard();
  const { isOpen, onOpen, onClose } = useDisclosure();

  const handlePrevious = () => {
    if (state.currentCardIndex > 0) {
      dispatch({ type: 'SET_CURRENT_CARD', payload: state.currentCardIndex - 1 });
    }
  };

  const handleNext = () => {
    if (state.currentCardIndex < state.flashcards.length - 1) {
      dispatch({ type: 'SET_CURRENT_CARD', payload: state.currentCardIndex + 1 });
    }
  };

  const handleShuffle = () => {
    dispatch({ type: 'SHUFFLE_CARDS' });
  };

  const handleReset = () => {
    dispatch({ type: 'RESET' });
  };

  const handleChangeSet = () => {
    dispatch({ type: 'RESET' });
  };

  if (state.flashcards.length === 0) {
    return null;
  }

  const currentCard = state.flashcards[state.currentCardIndex];
  const isFirstCard = state.currentCardIndex === 0;
  const isLastCard = state.currentCardIndex === state.flashcards.length - 1;

  return (
    <AnimatePresence>
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        exit={{ opacity: 0, y: -20 }}
        transition={{ duration: 0.5 }}
      >
        {/* Progress and Controls */}
        <HStack justify="space-between" mb={6}>
          <HStack spacing={4}>
            <motion.div
              whileHover={!isFirstCard ? { scale: 1.05 } : {}}
              whileTap={!isFirstCard ? { scale: 0.95 } : {}}
            >
              <Button
                colorScheme="gray"
                size="md"
                onClick={handlePrevious}
                disabled={isFirstCard}
                leftIcon={<Icon as={ChevronLeft} />}
                opacity={isFirstCard ? 0.5 : 1}
                cursor={isFirstCard ? 'not-allowed' : 'pointer'}
              >
                Previous
              </Button>
            </motion.div>

            <Text fontSize="lg" fontWeight="semibold" color="gray.700">
              {state.currentCardIndex + 1} of {state.flashcards.length}
            </Text>

            <motion.div
              whileHover={!isLastCard ? { scale: 1.05 } : {}}
              whileTap={!isLastCard ? { scale: 0.95 } : {}}
            >
              <Button
                colorScheme="gray"
                size="md"
                onClick={handleNext}
                disabled={isLastCard}
                rightIcon={<Icon as={ChevronRight} />}
                opacity={isLastCard ? 0.5 : 1}
                cursor={isLastCard ? 'not-allowed' : 'pointer'}
              >
                Next
              </Button>
            </motion.div>
          </HStack>

          <HStack spacing={2}>
            <motion.div
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              <Button
                colorScheme="purple"
                size="md"
                onClick={handleShuffle}
                leftIcon={<Icon as={Shuffle} />}
              >
                Shuffle
              </Button>
            </motion.div>

            <motion.div
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              <Button
                colorScheme="green"
                size="md"
                onClick={handleReset}
                leftIcon={<Icon as={RotateCcw} />}
              >
                Reset
              </Button>
            </motion.div>

            <motion.div
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              <Menu isOpen={isOpen} onClose={onClose}>
                <MenuButton
                  as={Button}
                  colorScheme="blue"
                  size="md"
                  onClick={onOpen}
                  rightIcon={<Icon as={ChevronDown} />}
                  leftIcon={<Icon as={FolderOpen} />}
                >
                  Change Set
                </MenuButton>
                <MenuList>
                  <MenuItem onClick={handleChangeSet}>
                    Load Different Set
                  </MenuItem>
                  <MenuItem onClick={handleChangeSet}>
                    Upload New Set
                  </MenuItem>
                </MenuList>
              </Menu>
            </motion.div>
          </HStack>
        </HStack>

        {/* Flashcard Display */}
        <Flashcard card={currentCard} />

        {/* Metadata Display */}
        <MetadataDisplay />
      </motion.div>
    </AnimatePresence>
  );
}
