import React from 'react';
import { motion } from 'framer-motion';
import { HelpCircle, CheckCircle } from 'lucide-react';
import { useFlashcard } from '../contexts/FlashcardContext';
import { Flashcard as FlashcardType } from '../contexts/FlashcardContext';

interface FlashcardProps {
  card: FlashcardType;
}

export function Flashcard({ card }: FlashcardProps) {
  const { state, dispatch } = useFlashcard();

  const handleToggleAnswer = () => {
    dispatch({ type: 'TOGGLE_ANSWER' });
  };

  return (
    <motion.div
      className="flashcard card min-h-96 relative"
      layout
      transition={{ duration: 0.3 }}
    >
      <div className={`flashcard-inner ${state.isAnswerVisible ? 'flipped' : ''}`}>
        {/* Front of card (Question) */}
        <div className="flashcard-front">
          <div className="text-center">
            <motion.div 
              className="mb-6"
              initial={{ scale: 0 }}
              animate={{ scale: 1 }}
              transition={{ delay: 0.1, type: "spring", stiffness: 200 }}
            >
              <HelpCircle className="mx-auto h-12 w-12 text-blue-600" />
            </motion.div>

            <h3 className="text-xl font-semibold text-gray-800 mb-4">Question</h3>

            <motion.div 
              className="text-lg text-gray-700 leading-relaxed mb-8"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ delay: 0.2 }}
            >
              {card.question}
            </motion.div>

            <motion.button
              className="btn btn-primary"
              onClick={handleToggleAnswer}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              Show Answer
            </motion.button>
          </div>
        </div>

        {/* Back of card (Answer) */}
        <div className="flashcard-back">
          <div className="text-center">
            <motion.div 
              className="mb-6"
              initial={{ scale: 0 }}
              animate={{ scale: 1 }}
              transition={{ delay: 0.1, type: "spring", stiffness: 200 }}
            >
              <CheckCircle className="mx-auto h-12 w-12 text-green-600" />
            </motion.div>

            <h3 className="text-xl font-semibold text-gray-800 mb-4">Answer</h3>

            <motion.div 
              className="text-lg text-gray-700 leading-relaxed mb-8"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ delay: 0.2 }}
            >
              {card.answer}
            </motion.div>

            <motion.button
              className="btn btn-success"
              onClick={handleToggleAnswer}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              Show Question
            </motion.button>
          </div>
        </div>
      </div>
    </motion.div>
  );
}
