import React from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  ChevronLeft, 
  ChevronRight, 
  Shuffle, 
  RotateCcw, 
  HelpCircle, 
  CheckCircle,
  Info
} from 'lucide-react';
import { useFlashcard } from '../contexts/FlashcardContext';
import { Flashcard } from './Flashcard';
import { MetadataDisplay } from './MetadataDisplay';

export function FlashcardSection() {
  const { state, dispatch } = useFlashcard();

  const handlePrevious = () => {
    if (state.currentCardIndex > 0) {
      dispatch({ type: 'SET_CURRENT_CARD', payload: state.currentCardIndex - 1 });
    }
  };

  const handleNext = () => {
    if (state.currentCardIndex < state.flashcards.length - 1) {
      dispatch({ type: 'SET_CURRENT_CARD', payload: state.currentCardIndex + 1 });
    }
  };

  const handleShuffle = () => {
    dispatch({ type: 'SHUFFLE_CARDS' });
  };

  const handleReset = () => {
    dispatch({ type: 'RESET' });
  };

  if (state.flashcards.length === 0) {
    return null;
  }

  const currentCard = state.flashcards[state.currentCardIndex];
  const isFirstCard = state.currentCardIndex === 0;
  const isLastCard = state.currentCardIndex === state.flashcards.length - 1;

  return (
    <AnimatePresence>
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        exit={{ opacity: 0, y: -20 }}
        transition={{ duration: 0.5 }}
      >
        {/* Progress and Controls */}
        <div className="flex justify-between items-center mb-6">
          <div className="flex items-center space-x-4">
            <motion.button
              className={`btn btn-secondary ${isFirstCard ? 'opacity-50 cursor-not-allowed' : ''}`}
              onClick={handlePrevious}
              disabled={isFirstCard}
              whileHover={!isFirstCard ? { scale: 1.05 } : {}}
              whileTap={!isFirstCard ? { scale: 0.95 } : {}}
            >
              <ChevronLeft className="w-5 h-5" />
            </motion.button>

            <span className="text-lg font-semibold text-gray-700">
              {state.currentCardIndex + 1} of {state.flashcards.length}
            </span>

            <motion.button
              className={`btn btn-secondary ${isLastCard ? 'opacity-50 cursor-not-allowed' : ''}`}
              onClick={handleNext}
              disabled={isLastCard}
              whileHover={!isLastCard ? { scale: 1.05 } : {}}
              whileTap={!isLastCard ? { scale: 0.95 } : {}}
            >
              <ChevronRight className="w-5 h-5" />
            </motion.button>
          </div>

          <div className="flex items-center space-x-2">
            <motion.button
              className="btn btn-purple"
              onClick={handleShuffle}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              <Shuffle className="w-5 h-5" />
            </motion.button>

            <motion.button
              className="btn btn-success"
              onClick={handleReset}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              <RotateCcw className="w-5 h-5" />
            </motion.button>
          </div>
        </div>

        {/* Flashcard Display */}
        <Flashcard card={currentCard} />

        {/* Metadata Display */}
        <MetadataDisplay />
      </motion.div>
    </AnimatePresence>
  );
}
